import React, { useState, useContext, useEffect } from 'react';
import TopNav from '../components/Operation/TopNav';
import NewOperation from '../components/Operation/NewOperation';
import ProfileView from '../components/Operation/ProfileView';
import BankAccount from '../components/Operation/BankAccountsView';
import OperationHistory from '../components/Operation/OperationHistory';
import Layout from '../components/Layout';
import UserContext from '../context/UserContext';
import Fetch from 'isomorphic-unfetch';
import Cookies from 'js-cookie';
import Router from 'next/router';
import Navbar2 from '../components/Navbar2';
import Topbar from '../components/topbar';
import Loader from '../components/Loader';
import Footer2 from '../components/Footer2';
import Head from 'next/head';

const pricing = props => {
	const { state, isUserAuth } = useContext(UserContext);

	const [isLogin, setIsLogin] = useState('');

	const [submitInfo, setSubmitInfo] = useState({
		shipToCountryId: 0,
		height: 0,
		length: 0,
		width: 0,
		dimensionUnit: 'CM',
		weightUnit: 'KG',
		shipTo: '',
		shipToPostal: '',
		shipToCity: '',
		weight: 0,
		productTotal: 0
	})

	useEffect(() => {
		if(document.getElementsByClassName('nav-list pricing').length > 0){
			for(let i=0; i<document.getElementsByClassName('nav-list pricing').length; i++) {
				document.getElementsByClassName('nav-list pricing')[i].classList.add("active");
			}
		}
	}, []);

	const _handleChangeShipToCountry = (e) =>
	{
		e.preventDefault();
		e.persist();
		setSubmitInfo((prev) => ({
			...prev,
			shipToCountryId: e.target.value,
		}))
	}

	return (
		<>
			<Layout>
				<div className='appear-animate'>
					<Navbar2 />
					<Topbar />
					<div className="banner">
						<div className="banner-title">
							<h1>ShipMymail Shipping Cost Calculator</h1>
						</div>
						<div>
							<form onSubmit={(e) => _onSubmit(e)}>
								<p>1. where do you want to send a package?</p>
								<div className="row">
									<div className="col-md-6">
										<select placeholder="Country" className="form-control" value={submitInfo.shipToCountryId} onChange={(e) => _handleChangeShipToCountry(e)}>
											<option value="0">Country</option>
											<option value="1">Canada</option>
											<option value="2">UnitedStates</option>
											<option value="3">UnitedKingdom</option>
										</select>
									</div>
									<div className="col-md-6">
										<input className="form-control" value={submitInfo.shipToPostal} onChange={(e) => _handleChangePostalCode(e)} placeholder="Postal Code" />
									</div>
								</div>
								<p>2. Shipping Weight & Dimensions & Value</p>
								<div className="row">
									<div className="col-md-5">
										<div className="row">
											<div className="col-md-6">
												<input className="form-control" value={submitInfo.weight} onChange={(e) => _handleChangeWeight(e)} placeholder="0" />
											</div>
											<div className="col-md-6">
												<select placeholder="Country" className="form-control" value={submitInfo.weightUnit} onChange={(e) => _handleChangeWeightUnit(e)}>
													<option value="IBS">Ibs</option>
													<option value="KG">Kg</option>
												</select>
											</div>
										</div>
									</div>
									<div className="col-md-7">
										<div className="row">
											<div className="col-md-3">
												<input className="form-control" value={submitInfo.height} onChange={(e) => _handleChangeHeight(e)} placeholder="0" />
											</div>
											<div className="col-md-3">
												<input className="form-control" value={submitInfo.length} onChange={(e) => _handleChangeLength(e)} placeholder="0" />
											</div>
											<div className="col-md-3">
												<input className="form-control" value={submitInfo.width} onChange={(e) => _handleChangeWidth(e)} placeholder="0" />
											</div>
											<div className="col-md-3">
												<select placeholder="Country" className="form-control">
													<option>Inch</option>
												</select>
											</div>
										</div>
									</div>
								</div>
								<div className="row">
									<div className="col-md-2">
										<input value={submitInfo.productTotal} className="form-control" onChange={(e) => _handleChangeProductTotal(e)} />
									</div>
									<div className="col-md-10"></div>
								</div>
								<div style={{textAlign:"center"}}>
									<button type="submit">CalCulate</button>
								</div>
							</form>
						</div>
						<div className="shpping-info">
							<div className="shipping-info-title">
								<h1>Your Shipping Options</h1>
							</div>
							<div className="shipping-info-content">
								<p>
									&nbsp;&nbsp;&nbsp;Disclaimer: Local currencies displayed are approximate. Final cost will be determined by 
									your credit card &nbsp;&nbsp;&nbsp;issuer or Paypal. Exchange rates flutuate on a daily basis.
								</p>
								<p>
									&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;price INCLUDES Tax, Fuel Surcharge and Customs Processing Fees
								</p>
							</div>
						</div>
					</div>
					<Footer2 />
					<style jsx>{`
						div,
						section {
							background: white;
						}
						.banner {
							padding: 60px 25%;
						}
						.banner-title h1 {
							font-size: 26px;
							color: #1e487c;
							margin-top: 10px;
							margin-bottom: 10px;
						}
						.shipping-info-title h1{
							font-size: 26px;
							text-align: center;
							margin-top: 10px;
							margin-bottom: 10px;
						}
						.form-control{
							font-size: 14px;
							border-radius: 4px;
							border: 1px solid black;
							margin-bottom: 15px;
						}
						.banner form p{
							margin-bottom: 8px;
						}
						.banner form button{
							width: 95%;
							padding: 9px 0px;
							background-color: #1e487c;
							border: none;
							border-radius: 4px;
							color: white;
							text-align: center;
							margin-top: 20px;
						}
					`}</style>
				</div>
			</Layout>
		</>
	);
};

export default pricing;
