import React, { useContext, useState, useEffect } from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Logincontext from '../../context/LoginContext';
import UserContext from '../../context/UserContext';
import AlertContext from '../../context/alert/alertContext';
import { FaEye, FaEyeSlash } from 'react-icons/fa';
import Navbar2 from '../Navbar2';
import Topbar from '../topbar';
import Footer2 from '../Footer2';

import Router, { withRouter } from 'next/router';
import Axios from 'axios';
import Link from 'next/link';
// import cookies from "next-cookies";
import Loader from '../Loader';
import Cookies from 'js-cookie';
import {
	NotificationContainer,
	NotificationManager,
} from 'react-notifications';

export const Login = () => {
	const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');
	const context = useContext(Logincontext);
	const { state, addToken, addName } = useContext(UserContext);
	const alertContext = useContext(AlertContext);
	const { setAlert, alerts } = alertContext;
	const [awaitData, setAwaitData] = useState(false);
	const [showPassword, setShowPassword] = useState(false);
	const [loading, setLoading] = useState(false);

	// useEffect(() => {
	//   NotificationManager.info("Please login to continue", "Hola");
	// }, []);
	const init = () => {
		const tok = Cookies.get('token');
		if (tok !== undefined) {
			window.location.href = '/operation';
		}
	};

	useEffect(() => {
		window.onpopstate = function (event) {
			window.location.href = '/';
		};
		init();
		setLoading(false);
	}, []);

	const handleLogin = async e => {
		e.preventDefault();
		const body = { email: username, password: password };

		setAwaitData(true);
		try {
			if (body.email === '' || body.password === '') {
				setAlert('Login error');
				setAwaitData(false);
			} else {
				const res = await Axios.post('http://localhost:5000/users/login', {data: body});
				// console.log(res, 'resss');
				// await addToken(res.data.token);
				// Cookies.set('token', res.data.token);
				console.log(res);
				localStorage.setItem("ship-token", res.data.token);
				localStorage.setItem("ship-username", res.data.name);
				if (router.pathname === '/' || router.pathname === '/index') {
					window.location.reload();
				} else {
					Router.push(router.pathname);
				}

				const header = {
					headers: {
						'x-auth-token': state.token,
					},
				};
				// const data = await Axios.get('https://api.moni.pe/api/auth', header);
				// await addName(data.data.name);
			}
		} catch (error) {
			if (error.response.data.msg === 'Your account has not been verified.') {
				window.location.href = '/resend';
			}
			setAlert(error.response.data.msg, 'danger');
			setAwaitData(false);
		}
	};

	const togglePassword = e => {
		e.preventDefault();
		setShowPassword(!showPassword);
	};

	if (awaitData === true) {
		return (
			<div className='form-container w-100 h-100'>
				<div className='loader'>
					<Loader />
				</div>
				<style>{`
        .form-container {
          height: 100vh !important;
          // background: #071127;
          }
        `}</style>
			</div>
		);
	}

	if (loading === true) {
		return (
			<div className='page-loader'>
				<div className='loader'>Cargando...</div>
			</div>
		);
	}

	return (
		<>
			<section className='page-section pt-20 pb-0 appear-animate' id='page'>
				<Navbar2 />
				<Topbar />
				<div className='home-content container'>
					<div className='home-text'>
						<div className='container align-center'>
							<div className='row'>
								<div className='col-sm-7 col-sm-push-3'>
									<form className='form white'>
										<h1>Sign in to enjoy Shopping from Canada</h1>
										<input
											type='email'
											name='usuario'
											id='usuario'
											className='input-sm bold'
											placeholder='email'
											pattern='.{3,100}'
											value={username}
											onChange={e => {
												setUsername(e.target.value);
											}}
										/>
										<i className='fa fa-user fa-2x campoform'></i>
										<input
											name='password'
											id='password'
											className='input-sm bold'
											placeholder='password'
											pattern='.{3,100}'
											type={showPassword ? 'text' : 'password'}
											value={password}
											onChange={e => {
												setPassword(e.target.value);
											}}
										/>

										<i
											className='fa fa-eye fa-2x campoform'
											onClick={togglePassword}
										/>
										<div>
											<button onClick={handleLogin}>
												SIGN IN
											</button>
										</div>

										<a href='/forgetpassword'><p style={{marginBottom: '3px', fontWeight: "normal"}}>Forget password?</p></a>
										<p style={{marginBottom: '3px', color: "black"}}> Don't have an account? &nbsp;&nbsp;
											<a href='/register'> 
												Sign Up Now
											</a>
										</p>
									</form>
								</div>
							</div>
						</div>
					</div>
				</div>
				<style jsx>{`
					.btn-icon {
						background: transparent;
						color: white;
					}
					.home-content {
						padding: 80px 0px 60px 0px;
						background: url("images/pay-bg.jpg");
						background-size: cover;
					}
					form {
						background-color: white;
						padding: 15px;
						text-align: center;
					}
					form h1 {
						font-size: 24px;
						color: #1e487c;
						font-weight: 600;
					}
					form input {
						border: 1px solid #f7f1f1 !important;
						color: black !important;
						border-radius: 3px !important;
					}
					form button {
						padding: 8px 25px;
						border:none;
						border-radius: 3px;
						background-color: #1e487c;
						margin-bottom: 30px;
					}
					form a {
						color: black !important;
					}
				`}</style>
				<Footer2 />
			</section>
		</>
	);
};

export default Login;
