import React, { useState, useContext, useEffect } from 'react';
import Layout from '../components/Layout';
import UserContext from '../context/UserContext';
import Navbar2 from '../components/Navbar2';
import Topbar from '../components/topbar';
import Footer2 from '../components/Footer2';
import DataTable from 'react-data-table-component';


const invoice = props => {
	const { state, isUserAuth } = useContext(UserContext);

	const [isLogin, setIsLogin] = useState('');
	const [pageInfo, setPageInfo] = useState();
	const data = [{
		name: 'Rara',
		profile: 'profile1',
		comment: 'comra'
	  },
	  {
		name: 'Dada',
		profile: 'profile2',
		comment: 'comda'
	  },
	  {
		name: 'Gaga',
		profile: 'profile1',
		comment: 'comga'
	  },
	  {
		name: 'Mama',
		profile: 'profile3',
		comment: 'comma'
	  },
	  {
		name: 'Papa',
		profile: 'profile4',
		comment: 'compa'
	  },
	  this.state = {
		selectedPage: 1,
		totalPages: 0,
		pageSize: 20
	  }
	]
	const columns = [
	{
		name: 'Title',
		selector: 'title',
		sortable: true,
	},
	{
		name: 'Year',
		selector: 'year',
		sortable: true,
		right: true,
	},
	];

	useEffect(() => {
		if(document.getElementsByClassName('nav-list dashboard').length > 0){
			for(let i=0; i<document.getElementsByClassName('nav-list dashboard').length; i++) {
				document.getElementsByClassName('nav-list dashboard')[i].classList.add("active");
			}
		}
	}, []);

	return (
		<>
			<Layout>
				<div className='appear-animate'>
					<Navbar2 />
					<Topbar />
					<div className="banner">
						<div className="container">
							<h1>Uploaded Invoices</h1>
							<DataTable
								title="Arnold Movies"
								columns={columns}
								data={data}
							/>
						</div>
					</div>
					<Footer2 />
					<style jsx>{`
						div,
						section {
							background: white;
						}
						.banner {
							padding-top: 100px;
							padding-bottom: 50px;
							text-align: center;
						}
						.banner h1 {
							font-size: 28px;
							color: #1e487c;
							margin-bottom: 20px;
						}
					`}</style>
				</div>
			</Layout>
		</>
	);
};

export default invoice;
