    },
    contextMenu: {
      backgroundColor: '#E91E63',
      fontColor: '#FFFFFF',
    },
    header: {
      fontSize: '12px',
      fontColor: '#FFFFFF',
      backgroundColor: '#363640',
    },
    rows: {
      fontColor: '#FFFFFF',
      backgroundColor: '#363640',
      borderColor: 'rgba(255, 255, 255, .12)',
      hoverFontColor: 'black',
      hoverBackgroundColor: 'rgba(0, 0, 0, .24)',
    },
    cells: {
      cellPadding: '10px',
    },
};

const customStyles = {
    headCells: {
      style: {
        background: '#5577',
        fontSize: '16px',
        fontStyle: 'italic',
        color: '#6f6'
      },
    },
    cells: {
      style: {
        color: '#00ccff',
        fontWeight: 'bold',
        background: '#232323',
      },
    },
    pagination: {
      style: {
        color: 'white'
      }
    },
};
  
