import React, { useState, useContext, useEffect } from 'react';
import Layout from '../components/Layout';
import UserContext from '../context/UserContext';
import Navbar2 from '../components/Navbar2';
import Topbar from '../components/topbar';
import Footer2 from '../components/Footer2';
import DataTable, { createTheme } from "react-data-table-component";
import Card from "@material-ui/core/Card";
import SortIcon from "@material-ui/icons/ArrowDownward";
import axios from "axios";

createTheme('solarized', {
	text: {
	  primary: 'white',
	  secondary: 'white',
	},
	background: {
	  default: '#223',
	},
	divider: {
	  default: '#79f',
	},
});

const columns = [
    {
      name: "Rank",
      selector: row => row.rank,
      sortable: true,
    },
    {
      name: "Address",
      selector: row => row.address,
    },
    {
      name: "Current Quantity",
      selector: row => row.current_quantity,
      //sortable: true,
      //right: true
    },
    {
      name: "Percentage",
      selector: row => row.percent,
      //right: true
    },
    {
      name: "Current Value ($)",
      selector: row => row.value,
      sortable: true,
      //right: true
    },
    {
      name: "Previous Quantity",
      selector: row => row.previous_quantity,
      //sortable: true,
      //right: true
    },
  ];
  
  const darkTheme = {
    title: {
      fontSize: '22px',
      fontColor: '#FFFFFF',
      backgroundColor: '#363640',
    },
    contextMenu: {
      backgroundColor: '#E91E63',
      fontColor: '#FFFFFF',
    },
    header: {
      fontSize: '12px',
      fontColor: '#FFFFFF',
      backgroundColor: '#363640',
    },
    rows: {
      fontColor: '#FFFFFF',
      backgroundColor: '#363640',
      borderColor: 'rgba(255, 255, 255, .12)',
      hoverFontColor: 'black',
      hoverBackgroundColor: 'rgba(0, 0, 0, .24)',
    },
    cells: {
      cellPadding: '10px',
    },
  };

  const customStyles = {
    headCells: {
      style: {
        background: '#5577',
        fontSize: '16px',
        fontStyle: 'italic',
        color: '#6f6'
      },
    },
    cells: {
      style: {
        color: '#00ccff',
        fontWeight: 'bold',
        background: '#232323',
      },
    },
    pagination: {
      style: {
        color: 'white'
      }
    },
  };
  
function Table() {
	let [totalSupplyData, setTotalSupplyData] = useState({});

	useEffect(async () => {
		try {
		const { data } = await axios.get('http://localhost:5000/users/getinvoices', {id: localStorage.getItem("ship-userId")});
			setTotalSupplyData( data.rows );
		console.log(data);
		} catch (error) {
			console.log(error)
		}
	}, []);

	return (
		<div className="App">
			<Card>
				<DataTable
					title = "EverRise"
					columns = {columns}
					data = {totalSupplyData}
					defaultSortFieldId = {1}
					sortIcon = {<SortIcon />}
					pagination
					theme = "solarized"
					customStyles = { customStyles }
				/>
			</Card>l
		</div>
	);
}


const invoice = props => {
	const { state, isUserAuth } = useContext(UserContext);

	const [isLogin, setIsLogin] = useState('');
	
	useEffect(() => {
		if(document.getElementsByClassName('nav-list dashboard').length > 0){
			for(let i=0; i<document.getElementsByClassName('nav-list dashboard').length; i++) {
				document.getElementsByClassName('nav-list dashboard')[i].classList.add("active");
			}
		}
	}, []);

	return (
		<>
			<Layout>
				<div className='appear-animate'>
					<Navbar2 />
					<Topbar />
					<div className="banner">
						<div className="container">
							<h1>Uploaded Invoices</h1>
							{/* <Table /> */}
						</div>
					</div>
					<Footer2 />
					<style jsx>{`
						div,
						section {
							background: white;
						}
						.banner {
							padding-top: 100px;
							padding-bottom: 50px;
							text-align: center;
						}
						.banner h1 {
							font-size: 28px;
							color: #1e487c;
							margin-bottom: 20px;
						}
					`}</style>
				</div>
			</Layout>
		</>
	);
};

export default invoice;
